from os import listdir, remove

class Signal:

    def __init__(self, location= r"C:\Users\User\Desktop\TestSignal"):
        self.location = location
        self.set2Working()

    def getSignal(self):
        return listdir(self.location)[0]

    def set2Working(self):
        signal = self.getSignal()
        if not signal.startswith("Working"):
            remove(fr"{self.location}\{signal}")
            with open(fr"{self.location}\Working.txt", "w") as wrk:
                wrk.write("")

    def set2Stop(self):
        signal = self.getSignal()
        remove(fr"{self.location}\{signal}")
        with open(fr"{self.location}\stop.txt", "w") as wrk:
            wrk.write("")

    def signal2Stop(self):
        return self.getSignal().lower().startswith("stop")